import React from "react";

function UserDataForm({ selectedUser, onSelectUser, onSaveData }) {
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    onSelectUser((prevUser) => ({
      ...prevUser,
      [name]: value,
    }));
  };

  const saveData = () => {
    console.log("Saving data:", selectedUser);
    onSaveData(selectedUser);
    window.alert("Changes saved successfully.");
  };

  return (
    <div className="user-form">
      {selectedUser ? (
        <>
          <span className="user-header">
            <input
              type="text"
              name="name"
              className="user-name-input"
              value={selectedUser.name || ""}
              placeholder="Enter user name"
              onChange={handleInputChange}
            />
          </span>
          <div className="user-data">
          <svg className="user-picture" xmlns="http://www.w3.org/2000/svg" x="0px" y="0px" width="67" height="67" viewBox="0 0 172 172" style={{"fill": "#26e07f"}}><g fill="none" fillRule="nonzero" stroke="none" strokeWidth="1" strokeLinecap="butt" strokeLinejoin="miter" strokeMiterlimit="10" strok="" stroke-dashoffset="0" font-family="none" font-weight="none" font-size="none" text-anchor="none" style="mix-blend-mode: normal"><path d="M0,172v-172h172v172z" fill="none"></path><g fill="#1fb141"><path d="M21.5,21.5v129h64.5v-32.25v-64.5v-32.25zM86,53.75c0,17.7805 14.4695,32.25 32.25,32.25c17.7805,0 32.25,-14.4695 32.25,-32.25c0,-17.7805 -14.4695,-32.25 -32.25,-32.25c-17.7805,0 -32.25,14.4695 -32.25,32.25zM118.25,86c-17.7805,0 -32.25,14.4695 -32.25,32.25c0,17.7805 14.4695,32.25 32.25,32.25c17.7805,0 32.25,-14.4695 32.25,-32.25c0,-17.7805 -14.4695,-32.25 -32.25,-32.25z"></path></g></g></svg>
            <form>
              <span>
                <label className="form-label" htmlFor="department">
                  Department:
                </label>
                <input
                  id="department"
                  type="text"
                  name="department"
                  value={selectedUser?.department || ""}
                  onChange={handleInputChange}
                />
              </span>
              <span>
                <label className="form-label" htmlFor="job-title">
                  Job Title:
                </label>
                <input
                  id="job-title"
                  type="text"
                  name="jobTitle"
                  value={selectedUser?.jobTitle || ""}
                  onChange={handleInputChange}
                />
              </span>
              <span>
                <label className="form-label" htmlFor="company">
                  Company:
                </label>
                <input
                  id="company"
                  type="text"
                  name="company"
                  value={selectedUser?.company || ""}
                  onChange={handleInputChange}
                />
              </span>
            </form>
          </div>
          <button onClick={saveData}>Save</button>
        </>
      ) : (
        <div>No data to show</div>
      )}
    </div>
  );
}

export default UserDataForm;
